/**
  * 首先我们定义一个Node的构造函数
  */
function Node(data) {
  this.left = null;
  this.right = null;
  this.data = data;
}

/**
  * 二叉树的创建
  * 我们规定用数字0表示为空节点
  */
function createTree (data) {
