function MyNew(Func,...args){
    // 新建一个原型为Func原型的对象
    let obj = Object.create(Func.prototype);
    // 执行构造函数,利用apply改变this指针指向obj,返回一个值
    let result = Func.apply(obj,...args)
    // 若该值是对象,则返回,若不是,返回执行过构造函数的obj
    return (typeof result === 'object') ? result : obj;
}

