            for (var j = i; j >= 0; j-=gap) {
                if (insert < array[j]) {
                    array[j+gap] = array[j];
                    index = j;
                }
            }
            array[index] = insert;
        }
        console.log(array);
        console.log("-----------------------");
        gap = Math.round(gap/2 - 0.1);
    }
    return array;
}
```

