class MinList<T> {
  private list:T[];
  constructor(){
    this.list = [];
  }

  add(data: T){
    this.list.push(data)
  }

  min(): T{
    let min: T = this.list[0];
    for (const iterator  of this.list) {
      min =  iterator < min ? iterator : min 
    }
    return min;
  }
}
