  let msgBtn = document.querySelector('#msgBtn');
  msgBtn.addEventListener('click', e => {
    dialog.showMessageBox({
      type: 'warning',
      title: '消息对话框标题',
      message: '描述',
      buttons: ['按键1','按键2']
    }).then(res => {
      console.log(res)
      console.log('根据按键返回的res是按键的下标')
    })
  })
</script>
</html>
